import React, { useState } from 'react';
import { AuthProvider, useAuth } from './shared/AuthContext';
import { LanguageProvider, useLanguage } from './shared/LanguageContext';
import Login from './tarea2-login/Login';
import LanguageSelector from './tarea1-selector-idioma/LanguageSelector';
import ProductList from './tarea1-selector-idioma/ProductList';
import CrudProductos from './tarea3-crud-productos/CrudProductos';
import './App.css';

type TabType = 'products' | 'crud';

const MainApp: React.FC = () => {
  const { user, logout, isLoading } = useAuth();
  const { t } = useLanguage();
  const [activeTab, setActiveTab] = useState<TabType>('products');

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-content">
          <div className="spinner">‚è≥</div>
          <p>{t('loading')}</p>
        </div>
      </div>
    );
  }

  if (!user) {
    return <Login />;
  }

  return (
    <div className="main-app">
      <header className="app-header">
        <div className="header-content">
          <h1>üè™ Sistema de Productos Integrado</h1>
          <div className="header-controls">
            <LanguageSelector />
            <div className="user-info">
              <span>üë§ {t('welcomeBack')}, {user.username}!</span>
              <button onClick={logout} className="logout-btn">
                üö™ {t('logout')}
              </button>
            </div>
          </div>
        </div>
      </header>

      <nav className="app-nav">
        <button
          className={`tab-btn ${activeTab === 'products' ? 'active' : ''}`}
          onClick={() => setActiveTab('products')}
        >
          üì¶ {t('productList')} (Tarea 1)
        </button>
        <button
          className={`tab-btn ${activeTab === 'crud' ? 'active' : ''}`}
          onClick={() => setActiveTab('crud')}
        >
          üè™ {t('productManagement')} (Tarea 3)
        </button>
      </nav>

      <main className="app-main">
        {activeTab === 'products' && <ProductList />}
        {activeTab === 'crud' && <CrudProductos />}
      </main>

      <footer className="app-footer">
        <p>
          <strong>Tarea 1:</strong> Selector de idioma + Lista de productos con foto obligatoria |{' '}
          <strong>Tarea 2:</strong> Login (pantalla inicial) |{' '}
          <strong>Tarea 3:</strong> CRUD conectado al "servidor" con persistencia por usuario
        </p>
      </footer>
    </div>
  );
};

function App() {
  return (
    <AuthProvider>
      <LanguageProvider>
        <MainApp />
      </LanguageProvider>
    </AuthProvider>
  );
}

export default App;
